import React, { useState } from "react";
import UploadForm from "../Upload/UploadForm";
import UploadVideoProgress from "./UploadVideoProgress";
import UploadSubtitleOrSong from "./UploadSubtitleOrSong";

const MainContent = () => {
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [processingStep, setProcessingStep] = useState<
    "analyzing" | "processing" | "finalizing" | "done" | null
  >(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);

  const handleFileSelect = (file: File) => {
    console.log(file);
    setSelectedFile(file);
    setUploading(true);
    setProcessingStep("analyzing");
    setProgress(0);

    const fakeProgress = setInterval(() => {
      setProgress((prevProgress) => {
        const newProgress = prevProgress + 10;

        if (newProgress >= 30 && newProgress < 60) {
          setProcessingStep("processing");
        } else if (newProgress >= 60 && newProgress < 100) {
          setProcessingStep("finalizing");
        } else if (newProgress >= 100) {
          clearInterval(fakeProgress);
          setUploading(false);
          setProcessingStep("done");
        }

        return newProgress;
      });
    }, 1000);
  };

  return (
    <main className="flex-grow">
      <div className="max-w-4xl mx-auto text-center py-12 font-inter">
        <h1 className="text-4xl font-bold text-gray-800 mb-6">
          AI-Powered{" "}
          <span className="text-transparent bg-gradient-emotion bg-clip-text">
            Emotion
          </span>{" "}
          Analysis
        </h1>
        <p className="text-lg text-gray-600 mb-10">
          Upload your video and let our AI analyze the emotional impact of each
          scene.
        </p>
        {!uploading && !selectedFile ? (
          <UploadForm onFileSelect={handleFileSelect} />
        ) : (
          // <UploadVideoProgress progress={progress} step={processingStep as 'analyzing' | 'processing' | 'finalizing ' |'done'} />
          <UploadSubtitleOrSong
            progress={progress}
            step={
              processingStep as
                | "analyzing"
                | "processing"
                | "finalizing "
                | "done"
            }
            selectedFile={selectedFile as File}
            setSelectedFile={setSelectedFile}
          />
        )}

        {/* Introduction */}
        <section className="mt-28">
          <h2 className="text-2xl font-semibold text-gray-700 mb-4">
            - Introduction -
          </h2>
          <p className="text-gray-500 text-base">
            Welcome to our AI-powered video analysis tool! Our AI models are
            designed to analyze your video scene-by-scene, detecting emotional
            intensity, analyzing key moments, and determining how each scene may
            impact viewers. Start your journey by uploading a video, and the AI
            will break down the emotional journey of your content and provide
            actionable insights.
          </p>
        </section>
      </div>
    </main>
  );
};

export default MainContent;
