// src/services/fileProcessingService.ts

import { ProcessingStatus, ProcessingStep } from "../utils/upload.types";

export class FileProcessingService {
  private pollTimer: NodeJS.Timer | null = null;
  private API_BASE_URL = "http://0.0.0.0:8080";

  public async checkProcessingStatus(
    fileId: string,
  ): Promise<ProcessingStatus> {
    try {
      const response = await fetch(
        `${this.API_BASE_URL}/api/processing/status/${fileId}`,
        {
          method: "GET",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          credentials: "include", // Include credentials if you're using sessions
          mode: "cors", // Explicitly state we want CORS
        },
      );

      if (!response.ok) {
        throw new Error("Failed to fetch processing status");
      }

      const data = await response.json();
      return {
        status: data.status as ProcessingStep,
        progress: data.progress,
        result: data.result,
        error: data.error,
      };
    } catch (error) {
      console.error("Error checking processing status:", error);
      throw error;
    }
  }

  public startPolling(
    fileId: string,
    onProgress: (status: ProcessingStatus) => void,
    onComplete: (result: any) => void,
    onError: (error: Error) => void,
  ): void {
    this.pollTimer = setInterval(async () => {
      try {
        const status = await this.checkProcessingStatus(fileId);
        onProgress(status);

        if (status.status === "completed") {
          this.stopPolling();
          onComplete(status.result);
        } else if (status.status === "failed") {
          this.stopPolling();
          onError(new Error(status.error || "Processing failed"));
        }
      } catch (error) {
        console.error("Polling error:", error);
        // Don't stop polling on first error, maybe add retry logic
        if (error instanceof Error) {
          onError(error);
        } else {
          onError(new Error("Unknown error during status check"));
        }
      }
    }, 2000);
  }

  public stopPolling(): void {
    if (this.pollTimer) {
      clearInterval(this.pollTimer);
      this.pollTimer = null;
    }
  }
}
