// src/components/Upload/components/ProgressBar.tsx

import React from "react";
import { ProcessingStep } from "../Upload/utils/upload.types";

interface ProgressBarProps {
  progress: number;
  fileSize: number;
  fileName: string;
  status: ProcessingStep;
  onCancel?: () => void;
}

const ProgressBar: React.FC<ProgressBarProps> = ({
  progress,
  fileSize,
  fileName,
  status,
  onCancel,
}) => {
  const getStatusColor = (status: ProcessingStep): string => {
    switch (status) {
      case "completed":
        return "bg-green-500";
      case "failed":
        return "bg-red-500";
      case "processing":
      case "analyzing":
        return "bg-blue-500";
      default:
        return "bg-gray-500";
    }
  };

  const formatFileSize = (bytes: number): string => {
    const units = ["B", "KB", "MB", "GB"];
    let size = bytes;
    let unitIndex = 0;
    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }
    return `${size.toFixed(2)} ${units[unitIndex]}`;
  };

  return (
    <div className="relative p-6 bg-gray-100 rounded-xl mt-5">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h1 className="font-medium">{fileName}</h1>
          <div className="text-left text-xs flex items-center space-x-3 text-gray-500 mt-1">
            <span>{formatFileSize(fileSize)}</span>
            <span>â€¢</span>
            <span className="capitalize">{status}</span>
          </div>
        </div>
        <div className="text-right font-semibold">{progress}%</div>
      </div>

      <div className="flex justify-between items-center">
        <div className="flex-grow h-2 bg-gray-200 rounded-full overflow-hidden">
          <div
            className={`h-full transition-all duration-300 ${getStatusColor(status)}`}
            style={{ width: `${progress}%` }}
          />
        </div>

        {onCancel && status !== "completed" && status !== "failed" && (
          <button
            onClick={onCancel}
            className="ml-4 text-sm text-gray-500 hover:text-gray-700"
          >
            Cancel
          </button>
        )}
      </div>
    </div>
  );
};

export default ProgressBar;
